"use strict";const{EventEmitter:EventEmitter}=require("events"),{ipcRenderer:ipcRenderer}=require("electron"),pkg=require("../package.json"),ipcFlag=`${pkg.name}@${pkg.version}`;class Extends extends EventEmitter{register(e,r){ipcRenderer.send(`${ipcFlag}:call`,"register",e,r)}unregister(e){ipcRenderer.send(`${ipcFlag}:call`,"unregister",e)}require(e){const r=ipcRenderer.sendSync(`${ipcFlag}:file`,e);if(!r)return null;try{return require(r)}catch(e){return console.error(e),null}}}module.exports=new Extends;