"use strict";const{EventEmitter:EventEmitter}=require("events"),ipc=require("@base/electron-base-ipc"),pkg=require("../package.json"),ipcFlag=`${pkg.name}@${pkg.version}`;class ProjectManager extends EventEmitter{add(e){ipc.send(`${ipcFlag}:call`,"add",e)}remove(e){ipc.send(`${ipcFlag}:call`,"remove",e)}open(e){ipc.send(`${ipcFlag}:call`,"open",e)}query(e){return ipc.sendSync(`${ipcFlag}:call`,"query",e)}}module.exports=new ProjectManager,ipc.on(`${ipcFlag}:emit`,(e,c,...r)=>{module.exports.emit(c,...r)});